mapper.select:
SELECT *
FROM ( SELECT *, ROW_NUMBER() OVER (ORDER BY root, step ASC) AS no FROM qna
ORDER BY root, step ASC ) AS q where 1= 1 And (content LIKE CONCAT('%',#{search},'%') OR title LIKE CONCAT('%',#{search},'%')) ORDER BY no ASC limit #{listNum} offset #{start_rownum}



  SELECT * FROM (
    SELECT
        *,
        ROW_NUMBER() OVER (ORDER BY regdate DESC) AS no
    FROM
        qna
) AS q
WHERE
    writer = #{nickname}
LIMIT #{listNum} OFFSET #{start_rownum}

